import React, { useState, useEffect } from 'react';
import './ImageLoader.css'; // import the CSS file for styles

function ImageLoader() {
    const [currentImage, setCurrentImage] = useState(null);
    const [isSpinning, setIsSpinning] = useState(false);

    const loadRandomImage = async () => {
        setIsSpinning(true); // start spin
        try {
            const response = await fetch('/local-images');
            if (response.ok) {
                setCurrentImage(response.url + '?' + new Date().getTime());
            } else {
                console.error('error loading image');
            }
        } catch (error) {
            console.error('error:', error);
        } finally {
            setTimeout(() => setIsSpinning(false), 1000); // end spin after 1s
        }
    };

    useEffect(() => {
        loadRandomImage();
    }, []);

    return (
        <div className="image-loader-container">
            <h1>random image loader nya~ :3</h1>
            {currentImage ? (
                <img
                    src={currentImage}
                    alt="random from cursed cat central"
                    className={`image ${isSpinning ? 'spin' : ''}`}
                />
            ) : (
                <p>loading image...</p>
            )}
            <br />
            <button onClick={loadRandomImage} className="button">load new image</button>
        </div>
    );
}

export default ImageLoader;
