import React, { useState, useEffect } from 'react';
import './ImageLoader.css';

function ImageLoader() {
    const [currentImage, setCurrentImage] = useState(null);
    const [incomingImage, setIncomingImage] = useState(null); // preload next image
    const [isTransitioning, setIsTransitioning] = useState(false);

    // fetches and preloads the next image
    const preloadNextImage = async () => {
        try {
            const response = await fetch('/local-images');
            if (response.ok) {
                const newImageUrl = response.url + '?' + new Date().getTime();
                setIncomingImage(newImageUrl);
            } else {
                console.error('error loading image');
            }
        } catch (error) {
            console.error('error:', error);
        }
    };

    // handles the full transition
    const loadRandomImage = () => {
        setIsTransitioning(true); // start spin-out animation

        // switch the image after spin-out completes
        setTimeout(() => {
            setCurrentImage(incomingImage);
            setIsTransitioning(false); // start spin-in animation
            preloadNextImage(); // prepare the next image
        }, 1000); // match this to the spin-out duration
    };

    // initial image load
    useEffect(() => {
        preloadNextImage().then(() => setCurrentImage(incomingImage));
    }, []); // run once on mount

    return (
        <div className="image-loader-container">
            <h1>random image loader nya~ :3</h1>
            {currentImage ? (
                <img
                    src={currentImage}
                    alt="random from cursed cat central"
                    className={`image ${isTransitioning ? 'batman-transition' : ''}`}
                />
            ) : (
                <p>loading image...</p>
            )}
            <br />
            <button onClick={loadRandomImage} className="button">load new image</button>
        </div>
    );
}

export default ImageLoader;
