import React, { useState, useEffect } from 'react';
import './ImageLoader.css';

function ImageLoader() {
    const [currentImage, setCurrentImage] = useState(null);
    const [nextImage, setNextImage] = useState(null); // for preloading
    const [isTransitioning, setIsTransitioning] = useState(false);

    const loadRandomImage = async () => {
        setIsTransitioning(true); // start transition (spin out and in)

        // fetch the next image but don’t display it yet
        try {
            const response = await fetch('/local-images');
            if (response.ok) {
                const newImageUrl = response.url + '?' + new Date().getTime();
                setNextImage(newImageUrl); // preload next image
            } else {
                console.error('error loading image');
            }
        } catch (error) {
            console.error('error:', error);
        }

        // after half of the animation (1s), switch to the new image
        setTimeout(() => {
            setCurrentImage(nextImage); // swap to preloaded image
            setIsTransitioning(false); // end transition (grow in)
        }, 1000); // adjust timing to match half the animation duration
    };

    useEffect(() => {
        loadRandomImage();
    }, []);

    return (
        <div className="image-loader-container">
            <h1>random image loader nya~ :3</h1>
            {currentImage ? (
                <img
                    src={currentImage}
                    alt="random from cursed cat central"
                    className={`image ${isTransitioning ? 'batman-transition' : ''}`}
                />
            ) : (
                <p>loading image...</p>
            )}
            <br />
            <button onClick={loadRandomImage} className="button">load new image</button>
        </div>
    );
}

export default ImageLoader;
