import React, { useState, useEffect } from 'react';

function ImageLoader() {
    const [currentImage, setCurrentImage] = useState(null);

    // function to load a random image from the local cache
    const loadRandomImage = async () => {
        try {
            const response = await fetch('/local-images');
            if (response.ok) {
                setCurrentImage(response.url + '?' + new Date().getTime()); // add timestamp to avoid caching
            } else {
                console.error('error loading image');
            }
        } catch (error) {
            console.error('error:', error);
        }
    };

    // function to refresh images from azure
    const refreshImages = async () => {
        try {
            const response = await fetch('http://localhost:5000/refresh-images');
            if (response.ok) {
                alert('images refreshed from azure successfully!');
            } else {
                alert('failed to refresh images');
            }
        } catch (error) {
            console.error('error refreshing images:', error);
            alert('an error occurred while refreshing images');
        }
    };

    // load a random image on page load
    useEffect(() => {
        loadRandomImage();
    }, []);

    return (
        <div>
            <h1>random image loader nya~ :3</h1>
            {currentImage ? (
                <img src={currentImage} alt="random from cursed cat central" style={styles.image} />
            ) : (
                <p>loading image...</p>
            )}
            <br />
            <button onClick={loadRandomImage} style={styles.button}>load new image</button>
            <button onClick={refreshImages} style={styles.button}>refresh images</button>
        </div>
    );
}

const styles = {
    image: {
        maxWidth: '90%',
        borderRadius: '15px',
        margin: '10px 0',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    },
    button: {
        backgroundColor: '#ff66b3',
        color: '#fff',
        border: 'none',
        borderRadius: '10px',
        padding: '10px 20px',
        cursor: 'pointer',
        fontSize: '1em',
        margin: '5px',
        transition: 'background-color 0.3s',
    },
};

export default ImageLoader;
