import React, { useState, useEffect } from 'react';

function ImageLoader() {
    const [currentImage, setCurrentImage] = useState(null);

    const loadRandomImage = async () => {
        try {
            const response = await fetch('/local-images');
            if (response.ok) {
                setCurrentImage(response.url + '?' + new Date().getTime());
            } else {
                console.error('error loading image');
            }
        } catch (error) {
            console.error('error:', error);
        }
    };

    useEffect(() => {
        loadRandomImage();
    }, []);

    return (
        <div>
            <h1>random image loader nya~ :3</h1>
            {currentImage ? (
                <img src={currentImage} alt="random from cursed cat central" style={styles.image} />
            ) : (
                <p>loading image...</p>
            )}
            <br />
            <button onClick={loadRandomImage} style={styles.button}>load new image</button>
            
        </div>
    );
    
}

const styles = {
    image: {
        maxWidth: '90%',
        borderRadius: '15px',
        margin: '10px 0',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    },
    button: {
        backgroundColor: '#ff66b3',
        color: '#fff',
        border: 'none',
        borderRadius: '10px',
        padding: '10px 20px',
        cursor: 'pointer',
        fontSize: '1em',
        transition: 'background-color 0.3s',
    },
};

export default ImageLoader;
